import React, {ChangeEvent, KeyboardEvent, useState} from 'react';
import {FilterValueType} from "./App";

type TasksType = {
    id: string,
    title: string,
    isDone: boolean
}

type TodolistProps = {
    todolistTitle: string
    button: string
    tasks: Array<TasksType>
    removeTask: (id: string) => void
    changeFilter: (value: FilterValueType) => void
    addTasks: (title: string) => void
}

export function Todolist(props: TodolistProps) {
    let [newTasktTitle, setNewTasktTitle] = useState("")

    let onAddTasksHandler = () => {
        props.addTasks(newTasktTitle)
        setNewTasktTitle("")
    }
    let onChangeHandler = (e: ChangeEvent<HTMLInputElement>) => {
        setNewTasktTitle(e.currentTarget.value)
    }

    let onKeyPresHandler = (e: KeyboardEvent<HTMLInputElement>) => {
        if (e.charCode === 13) {
            onAddTasksHandler()
        }
    }

    let onAllClickHandler = () => {
        props.changeFilter("All")
    }
    let onActiveClickHandler = () => {
        props.changeFilter("Active")
    }

    let onCompletedClickHandler = () => {
        props.changeFilter("Completed")
    }

    return (
        <div>
            <h3>{props.todolistTitle}</h3>
            <div>
                <input value={newTasktTitle}
                       onChange={onChangeHandler}
                       onKeyPress={onKeyPresHandler}
                />

                <button onClick={onAddTasksHandler}>{props.button}</button>
            </div>
            <ul>
                {
                    props.tasks.map(t => {
                        let onClickHandler = () => {props.removeTask(t.id)}

                        return (
                            <li key={t.id}>
                            <input type="checkbox" checked={t.isDone}/> <span>{t.title}</span>
                            <button onClick={onClickHandler}>-x-</button>
                        </li>
                        )
                    })
                }
            </ul>

            <div>
                <button onClick={onAllClickHandler}>All</button>
                <button onClick={onActiveClickHandler}>Active</button>
                <button onClick={onCompletedClickHandler}>Completed</button>
            </div>
        </div>
    )
}
